import Foundation

struct Tile: Hashable, Identifiable {
    let id = UUID()
    var letter: String
    let column: Int

    init(column: Int, letter forcedLetter: String? = nil) {
        // swiftlint:disable:next line_length
        self.letter = forcedLetter != nil ? forcedLetter! : String("".randomElement()!)
        self.column = column
    }
}
